import React from 'react';
import 'antd/dist/antd.css';
import './App.css';

const { Card } = antd;

const { Meta } = Card;


export default function DisplayCard(props) {
    return (
        <div>
            <div className="site-card-border-less-wrapper card" style={{
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
            }}>
                <Card hoverable
                    style={{ width: 240 }}
                    cover={<img alt="example" src={props.data.img} />}>
                    <Meta title={props.data.name} description={props.data.weight} grams />
                </Card>
            </div>
        </div>
    )
}
