import React, { Component } from 'react';
import './App.css';
import { Input } from 'antd';
import DisplayCard from './DisplayCard';
import axios from 'axios';

const { Search } = Input;

const api = axios.create({
    baseURL: `http://localhost:5000/api`
})


class MainPage extends Component {

    constructor(props) {
        super(props);
        this.state = {
            fruitData: [],
            submitted: false,
        }
    }

    getFruit = async (query) => {
        try {
            const res = await api.get(`/${query}`)
            if (res.ok) {
                this.setState({ fruitData: res.json, submitted: true })
            } else {
                this.sendError();
            }
        } catch (err) {
            console.log(err);
            this.sendError();
        }
    }

    sendError = () => {
        this.setState({
            fruitData: [],
            submitted: true
        })
    }


    render() {
        let { fruitData, submitted } = this.state;
        return (
            <div>

                <div className="title">
                    <h1>Fruit Finder</h1>
                </div>

                <div className="search-bar">
                    <Search
                        placeholder="Enter a fruit"
                        enterButton="Search"
                        size="medium"
                        onSearch={query => this.getFruit(query)} />
                </div>

                {submitted && fruitData.length < 1 ? (
                    <div className="not-found">
                        <h1>Not found! Please search again.</h1>
                    </div>
                ) : (
                        <div className="card-container">
                            {fruitData.map((desc) => {
                                return (
                                    <DisplayCard key={desc.name} data={desc} />
                                )
                            })}
                        </div>
                    )
                }

            </div>
        )
    }
}

export default MainPage;
