import React, { Component } from 'react';
import './App.css';


const FruitsPage = () => {

    const [query, setQuery] = useState('');

    return (
        <div>

            <div className="title">
                <h1>Fruit Finder</h1>
            </div>

            <div className="search-bar">
                <form id="search-bar" onSubmit={query => this.getFruit(query)}>
                    <input type="text" placeholder="Enter a fruit" value={query} onChange={e => setQuery(e.target.value)} />
                    <button>Search</button>
                </form>
            </div>

        </div>
    )
}

export default FruitsPage;
