import React, { Component } from 'react';
import './App.css';
import { Input } from 'antd';
import DisplayCard from './DisplayCard';

const { Search } = Input;


class MainPage extends Component {

    constructor(props) {
        super(props);
        this.state = {
            fruitData: [],
            submitted: false,
        }
    }

    getFruit = (query) => {
        fetch(`http://localhost:5000/api/${query}`).then((response) => {
            if (response.ok) {
                return response.json();
            } else {
                throw new Error('Something went wrong');
            }
        })
            .then((responseJson) => {
                this.setState({
                    fruitData: responseJson,
                    submitted: true
                })
                    .catch((error) => {
                        console.log(error);
                    })
            })
    }


    render() {
        let { fruitData, submitted } = this.state;
        return (
            <div>

                <div className="title">
                    <h1>Fruit Finder</h1>
                </div>

                <div className="search-bar">
                    <Search
                        placeholder="Enter a fruit"
                        enterButton="Search"
                        size="medium"
                        onSearch={query => this.getFruit(query)} />
                </div>

                {submitted && fruitData.length < 1 ? (
                    <div className="not-found">
                        <h1>Not found! Please search again.</h1>
                    </div>
                ) : (
                        <div className="card-container">
                            {fruitData.map((desc) => {
                                return (
                                    <DisplayCard key={desc.name} data={desc} />
                                )
                            })}
                        </div>
                    )
                }

            </div>
        )
    }
}

export default MainPage;
