import React, { Component } from 'react';
import './App.css';
import { Input } from 'antd';

const { Search } = Input;


class FruitsPage extends Component {

    constructor(props) {
        super(props);
        this.state = {
            fruitsData: [],
            submitted: false
        }
    }

    getFruit = (query) => {
        try {
            if (query) {
                const response = fetch(`http://localhost:5000/api/${query}`).then(response => response.json()).then(jsondata => console.log(jsondata))
            }
        } catch (err) {
            console.log(err);
            this.handleError();
        }
    }



    render() {
        return (
            <div>

                <div className="title">
                    <h1>Fruit Finder</h1>
                </div>

                <div className="search-bar">
                    <Search
                        placeholder="Enter a fruit"
                        enterButton="Search"
                        size="medium"
                        onSearch={query => this.getFruit(query)} />
                </div>

            </div>
        )
    }
}

export default FruitsPage;
