import React, { Component } from 'react';
import './App.css';


class FruitsPage extends Component {

    constructor(props) {
        super(props);
        this.state = {
            fruitsData: [],
            submitted: false
        }
    }

    getFruit = async (query) => {
        try {
            if (query) {
                const response = fetch(`https://localhost5000/api/${query}`)
            }
        } catch (err) {
            console.log(err);
            this.handleError();
        }
    }



    render() {
        return (
            <div>

                <div className="title">
                    <h1>Fruit Finder</h1>
                </div>

                <div className="search-bar">
                    <form id="search-bar" onSubmit={query => this.getFruit(query)}>
                        <input type="text" placeholder="Enter a fruit" value="" onChange="" />
                        <button>Search</button>
                    </form>
                </div>

            </div>
        )
    }
}

export default FruitsPage;
