import React, { Component } from 'react';
import './App.css';
import { Input } from 'antd';

const { Search } = Input;


class FruitsPage extends Component {

    constructor(props) {
        super(props);
        this.state = {
            fruitData: [],
            submitted: false
        }
    }

    getFruit = (query) => {
        try {
            if (query) {
                const response = fetch(`http://localhost:5000/api/${query}`)
                    .then(res => res.json())
                    .then((jsondata) => {
                        this.setState({
                            fruitData: jsondata,
                            submitted: true
                        });
                        console.log(fruitData);
                    });

            } else {
                this.sendError();
            }
        } catch (err) {
            console.log(err);
            this.sendError();
        }
    }

    render() {
        return (
            <div>

                <div className="title">
                    <h1>Fruit Finder</h1>
                </div>

                <div className="search-bar">
                    <Search
                        placeholder="Enter a fruit"
                        enterButton="Search"
                        size="medium"
                        onSearch={query => this.getFruit(query)} />
                </div>

            </div>
        )
    }
}

export default FruitsPage;
